.doc-md h3 {
  color: #873bf4;
  border-bottom: 1px solid #f0f0f0;
  margin: 1.2em 0 0.4em;
}

.doc-md p {
  margin: 0.6em 0;
}

.doc-md h4 {
  border-bottom: 1px solid #f0f0f0;
}

.doc-md h4::before {
  content: '> ';
  opacity: 0.5;
}

.doc-md .ant-badge p {
  line-height: 20px;
}

